import java.util.*;
public class ScannerEx {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner s=new Scanner(System.in);
		do
		{
			System.out.println("shape:");
			String s1=s.next();
			System.out.println(s1);
		}while(s.hasNext());

	}

}
